content:
/*
 * Copyright 2018 the original author or authors.
 *
 * Licensed under the Apache License, Version 2.0 (the "License");
 * you may not use this file except in compliance with the License.
 * You may obtain a copy of the License at
 *
 *     http://www.apache.org/licenses/LICENSE-2.0
 *
 * Unless required by applicable law or agreed to in writing, software
 * distributed under the License is distributed on an "AS IS" BASIS,
 * WITHOUT WARRANTIES OR CONDITIONS OF ANY KIND, either express or implied.
 * See the License for the specific language governing permissions and
 * limitations under the License.
 */
'use strict'

/**
 * JSONBatch:
 * this module exports some useful definition and utility related to
 * the JSONBatch definition for CloudEvents.
 */

/**
 * Get a reference to cloudevent CloudEvent class.
 *
 * @see Validator
 */
const CloudEvent = require('./cloudevent') // get cloudevent from here

/**
 * Get a reference to cloudevent Validator class.
 *
 * @see Validator
 */
const V = require('./validator') // get validator from here

/**
 * JSONBatch implementation.
 *
 * @see https://github.com/cloudevents/spec/blob/master/json-format.md#4-json-batch-format
 */
class JSONBatch {
  /**
   * Create a new instance of a JSONBatch object.
   *
   * Note that instancing is not allowed for this class because all its methods are static.
   *
   * @throws {Error} because instancing not allowed for this class
   */
  constructor () {
    throw new Error('Instancing not allowed for this class')
  }

  /**
   * Return the MIME Type for CloudEvent intances
   * batched into a single JSON document (array),
   * using the JSON Batch Format
   *
   * @static
   * @return {string} the value
   */
  static mediaType () {
    return 'application/cloudevents-batch+json'
  }

  /**
   * Validate the given JSONBatch.
   *
   * @static
   * @param {!object[]|object} batch the JSONBatch (array) to validate, or a single CloudEvent instance
   * @param {object} [options={}] containing: strict (boolean, default false) to validate it in a more strict way
   * @return {object[]} an array of (flattened, non null) validation errors, or at least an empty array
   */
  static validateBatch (batch, { strict = false } = {}) {
    if (V.isUndefinedOrNull(batch)) {
      return [new Error('JSONBatch undefined or null')]
    }

    // standard validation
    const ve = [] // validation errors
    if (V.isArray(batch)) {
      // additional validation on nested items (any not null item)
      const itemsValidation = batch.filter((i) => V.isDefinedAndNotNull(i)
      ).map((i) => {
        const ceValidation = CloudEvent.validateEvent(i, { strict })
        if (ceValidation.length > 0) {
          // return validation errors found
          return ceValidation
        }
      })
      ve.push(...itemsValidation)
    } else if (CloudEvent.isCloudEvent(batch)) {
      // validate the given (single) CloudEvent instance or subclass
      // in strict mode this is a validation error anyway, so add it
      if (strict === true) {
        ve.push(new TypeError("The argument 'batch' must be an array, instead got a CloudEvent instance (or a subclass)"))
      }
      ve.push(...CloudEvent.validateEvent(batch, { strict }))
    } else {
      return [new TypeError(`The argument 'batch' must be an array or a CloudEvent instance (or a subclass), instead got a '${typeof batch}'`)]
    }

    const veFiltered = ve.filter((i) => {
      return (V.isArray(i) || V.isError(i))
    }).reduce((acc, x) => acc.concat(x), []) // same as flat/flatMap

    return veFiltered
  }

  /**
   * Tell the given JSONBatch, if it's valid.
   *
   * See {@link CloudEvent.validateBatch}.
   *
   * @static
   * @param {!object} batch the JSONBatch to validate
   * @param {object} [options={}] containing: strict (boolean, default false) to validate it in a more strict way
   * @return {boolean} true if valid, otherwise false
   */
  static isValidBatch (batch, { strict = false } = {}) {
    const validationErrors = JSONBatch.validateBatch(batch, { strict })
    const size = V.getSize(validationErrors)
    return (size === 0)
  }

  /**
   * Tell the given object, if it's a JSONBatch (or at least an empty one).
   *
   * @static
   * @param {!object} batch the JSONBatch to check
   * @return {boolean} true if it's an array, otherwise false
   * @throws {Error} if batch is undefined or null
   */
  static isJSONBatch (batch) {
    if (V.isUndefinedOrNull(batch)) {
      throw new Error('JSONBatch undefined or null')
    }
    return V.isArray(batch)
  }

  /**
   * Generator to iterate across all CloudEvent instances in the JSONBatch.
   *
   * @static
   * @param {!object} batch the JSONBatch to iterate
   * @param {object} [options={}] optional processing attributes:
   *        onlyValid (boolean, default false) to extract only valid instances
   *        strict (boolean, default false) to validate it in a more strict way
   * @return {object} a CloudEvent (if any)
   * @throws {Error} if batch is undefined or null
   * @throws {TypeError} if batch is not a JSONBatch
   */
  static * getEvent (batch, {
    onlyValid = false,
    strict = false
  } = {}) {
    if (!JSONBatch.isJSONBatch(batch)) {
      throw new TypeError('The given batch is not a JSONBatch')
    }

    const itemsFiltered = batch.filter((i) => V.isDefinedAndNotNull(i) && CloudEvent.isCloudEvent(i))
    for (const i of itemsFiltered) {
      if (onlyValid === false) {
        yield i
      } else {
        // return only if it's a valid instance
        if (CloudEvent.isValidEvent(i, { strict })) {
          yield i
        }
      }
    }
  }

  /**
   * Return any not null CloudEvent instance from the given object.
   *
   * @static
   * @param {!object} batch the JSONBatch to extract CloudEvent instances (if any)
   * @param {object} [options={}] optional processing attributes:
   *        onlyValid (boolean, default false) to extract only valid instances
   *        strict (boolean, default false) to validate it in a more strict way
   * @return {object[]} processed events, as an array
   * @throws {Error} if batch is undefined or null, or an option is undefined/null/wrong
   * @throws {TypeError} if batch is not a JSONBatch
   */
  static getEvents (batch, {
    onlyValid = false,
    strict = false
  } = {}) {
    if (!JSONBatch.isJSONBatch(batch)) {
      throw new TypeError('The given batch is not a JSONBatch')
    }

    const ce = [] // CloudEvent instances
    // get values from the generator function, to simplify logic here
    for (const val of JSONBatch.getEvent(batch, { onlyValid, strict })) {
      ce.push(val)
    }

    return ce
  }

  /**
   * Serialize the given JSONBatch in JSON format.
   * Note that standard CloudEvent serialization will be called
   * for any CloudEvent instance, nothing other;
   * so options are the same used in CloudEvent related method.
   *
   * See {@link CloudEvent.serializeEvent}.
   *
   * @static
   * @param {!object[]} batch the JSONBatch (so a CloudEvent array instance) to serialize
   * @param {object} [options={}] optional serialization attributes
   *        Additional options valid here:
   *        logError (boolean, default false) to log to console serialization errors
   *        throwError (boolean, default false) to throw serialization errors
   * @return {string} the serialized JSONBatch, as a string
   * @throws {Error} if batch is undefined or null, or an option is undefined/null/wrong
   * @throws {TypeError} if batch is not a JSONBatch
   */
  static serializeEvents (batch, options = {}) {
    if (!JSONBatch.isJSONBatch(batch)) {
      throw new TypeError('The given batch is not a JSONBatch')
    }

    let ser = '[' // serialized CloudEvent instances
    let num = 0 // number of serialized CloudEvent

    // get values from the generator function, to simplify logic here
    for (const val of JSONBatch.getEvent(batch, options)) {
      ser += ((num > 0) ? ', ' : '')
      ser += ((options.prettyPrint === true) ? '\n' : '')
      try {
        ser += CloudEvent.serializeEvent(val, options)
        num++
      } catch (e) {
        ser += 'null' // as a fallback placeholder
        if (options.logError === true) {
          console.error(e)
        }
        if (options.throwError === true) {
          const msg = `Unable to serialize CloudEvent instance number ${num}, error detail: ${e.message}`
          throw new Error(msg)
        }
      }
    }

    if (options.prettyPrint === true) {
      ser += '\n'
    }
    ser += ']'

    return ser
  }

  /**
   * Deserialize/parse the given JSONBatch from JSON format.
   * Note that standard CloudEvent deserialization will be called
   * for any CloudEvent instance, nothing other;
   * so options are the same used in CloudEvent related method.
   *
   * See {@link CloudEvent.deserializeEvent}.
   *
   * @static
   * @param {!string} ser the serialized JSONBatch to parse/deserialize
   * @param {object} [options={}] optional deserialization attributes
   *        Additional options valid here:
   *        logError (boolean, default false) to log to console deserialization errors
   *        throwError (boolean, default false) to throw serialization errors
   * @return {object[]} the deserialized batch as a JSONBatch (so a CloudEvent array instance)
   * @throws {Error} if ser is undefined or null, or an option is undefined/null/wrong
   * @throws {Error} in case of JSON parsing error
   * @throws {TypeError} if ser is not a JSONBatch representation
   */
  static deserializeEvents (ser, options = {}) {
    if (!V.isStringNotEmpty(ser)) throw new Error(`Missing or wrong serialized data: '${ser}' must be a string and not a: '${typeof ser}'.`)

    // first deserialize to normal object instances
    let deser = null
    try {
      deser = JSON.parse(ser)
    } catch (e) {
      if (options.logError === true) {
        console.error(e)
      }
      if (options.throwError === true) {
        const msg = `Unable to deserialize the given string to JSONBatch, error detail: ${e.message}`
        throw new Error(msg)
      }
    }
    if (!V.isArray(deser)) {
      throw new TypeError('The given string is not an array representation')
    }

    // then build CloudEvent instances from any not null object that seems compatible
    const itemsFiltered = deser.filter((i) => V.isDefinedAndNotNull(i) && V.isObjectPlain(i))
    const batch = []
    let num = 0 // number of created CloudEvent
    for (const i of itemsFiltered) {
      // create a CloudEvent instance from the current object (if possible)
      try {
        const extensions = V.getObjectFilteredProperties(i, CloudEvent.isExtensionProperty)
        // note that strict is handleg both as strict and inside extensions, but it's good the same
        const ce = new CloudEvent(i.id, i.type, i.source, i.data, {
          time: i.time,
          datacontentencoding: i.datacontentencoding,
          datacontenttype: i.datacontenttype,
          schemaurl: i.schemaurl,
          subject: i.subject,
          strict: CloudEvent.getStrictExtensionOfEvent(i) || options.strict
        },
        extensions
        )
        if (V.isUndefinedOrNull(options.onlyValid) ||
          options.onlyValid === false ||
          (options.onlyValid === true && CloudEvent.isValidEvent(ce, { strict: options.strict }))
        ) {
          batch.push(ce)
          num++
        }
      } catch (e) {
        if (options.logError === true) {
          console.error(e)
        }
        if (options.throwError === true) {
          const msg = `Unable to create CloudEvent instance number ${num}, error detail: ${e.message}`
          throw new Error(msg)
        }
      }
    }

    return batch
  }
}

module.exports = JSONBatch

/*
 * Copyright 2018 the original author or authors.
 *
 * Licensed under the Apache License, Version 2.0 (the "License");
 * you may not use this file except in compliance with the License.
 * You may obtain a copy of the License at
 *
 *     http://www.apache.org/licenses/LICENSE-2.0
 *
 * Unless required by applicable law or agreed to in writing, software
 * distributed under the License is distributed on an "AS IS" BASIS,
 * WITHOUT WARRANTIES OR CONDITIONS OF ANY KIND, either express or implied.
 * See the License for the specific language governing permissions and
 * limitations under the License.
 */
'use strict'

/**
 * Transformers:
 * this module exports some useful generic functions for the transformation of objects.
 */

/**
 * Get a reference to cloudevent Validator class.
 *
 * See {@link Validator}.
 */
const V = require('./validator') // get validator from here

/** Get the host name where this code is runninng */
const hostname = require('os').hostname()

/** Get the process id (pid) where this code is runninng */
const pid = require('process').pid

/**
 * Useful Transformations for CloudEvent objects.
 *
 * Note that all methods here are static, so no need to instance this class;
 * see it as an Utility/Companion class.
 */
class Transformer {
  /**
   * Create a new instance of a Transformer object.
   *
   * Note that instancing is not allowed for this class because all its methods are static.
   *
   * @throws {Error} because instancing not allowed for this class
   */
  constructor () {
    throw new Error('Instancing not allowed for this class')
  }

  /**
   * Utility function that return a dump of the given object.
   *
   * @static
   * @param {(?object|Map|Set)} obj the object to dump
   * @param {string} [name='noname'] the name to assign in the returned string, or 'noname' as default value
   * @return {string} the dump of the object or a message when obj is undefined/null/not an object
   */
  static dumpObject (obj, name = 'noname') {
    if (V.isUndefined(obj)) {
      return `${name}: undefined`
    } else if (V.isNull(obj)) {
      return `${name}: null`
    } else if (V.isObject(obj) || V.isKeyedCollection(obj)) {
      return `${name}: ${JSON.stringify(obj)}`
    } else {
      return `${name}: '${obj.toString()}'`
    }
  }

  /**
   * Utility function that parse a string representation
   * (compatible with the CloudEvent standard) of the given timestamp (Date)
   * and returns it (if possible).
   *
   * Note that the value returned could be adjusted with the current timezone offset.
   *
   * @static
   * @param {!string} obj the timestamp/date to parse (as a string)
   * @param {number} [timezoneOffset=0] a timezone offset to add (in msec, default 0)
   * @return {object} the parsed version, as a timestamp (Date) object, if possible
   * @throws {TypeError} if obj is undefined or null, or is not a string
   */
  static timestampFromString (obj, timezoneOffset = 0) {
    if (!V.isStringNotEmpty(obj)) {
      throw new TypeError(`Missing or wrong timestamp: '${obj}' must be a string and not a: '${typeof obj}'.`)
    }
    const timestampMsec = Date.parse(obj)
    return new Date(timestampMsec + timezoneOffset)
  }

  /**
   * Utility function that return a string representation
   * (compatible with the CloudEvent standard)
   * of the given timestamp (Date), or the current one will be used.
   *
   * Note that the value returned is in the UTC format.
   *
   * @static
   * @param {?object} obj the timestamp/date to convert, or the current one if not defined or null
   * @return {string} the string representation of the object
   * @throws {TypeError} if obj is undefined or null, or is not a Date instance
   */
  static timestampToString (obj) {
    let timestamp = obj
    if (V.isUndefinedOrNull(timestamp)) {
      timestamp = new Date()
    }
    if (!V.isDateValid(timestamp)) {
      throw new TypeError(`Missing or wrong timestamp: '${timestamp}' must be a date and not a: '${typeof timestamp}'.`)
    }
    return timestamp.toISOString()
  }

  /**
   * Utility function that parse a number representation
   * of the given timestamp (Date)
   * and returns it (if possible).
   *
   * Note that the value returned could be adjusted with the current timezone offset.
   *
   * @static
   * @param {!number} obj the timestamp/date to parse (as a number)
   * @param {number} [timezoneOffset=0] a timezone offset to add (in msec, default 0)
   * @return {object} the parsed version, as a timestamp (Date) object, if possible
   * @throws {TypeError} if obj is undefined or null, or is not a number
   */
  static timestampFromNumber (obj, timezoneOffset = 0) {
    if (!V.isNumber(obj)) {
      throw new TypeError(`Missing or wrong timestamp: '${obj}' must be a number and not a: '${typeof obj}'.`)
    }
    return new Date(obj + timezoneOffset)
  }

  /**
   * Utility function that return a number representation
   * of the given timestamp (Date), or the current one will be used.
   *
   * Note that the value returned is in the UTC format.
   *
   * @static
   * @param {?object} obj the timestamp/date to convert, or the current one if not defined or null
   * @return {number} the number representation of the object
   * @throws {TypeError} if obj is not a Date instance
   */
  static timestampToNumber (obj) {
    let timestamp = obj
    if (V.isUndefinedOrNull(timestamp)) {
      timestamp = new Date()
    }
    if (!V.isDateValid(timestamp)) {
      throw new TypeError(`Wrong timestamp: '${timestamp}' must be a date and not a: '${typeof timestamp}'.`)
    }
    return timestamp.getTime()
  }

  /**
   * Utility function that encodes a string
   * in base64 (compatible with the CloudEvent standard).
   *
   * @static
   * @param {?string} obj the string to encode, or '' if not defined or null
   * @return {string} the string encoded in base64
   * @throws {TypeError} if str is not a string instance
   */
  static stringToBase64 (obj) {
    let str = obj
    if (V.isUndefinedOrNull(obj)) {
      str = ''
    }
    if (!V.isString(str)) {
      throw new TypeError(`Missing or wrong argument: '${str}' must be a string and not a: '${typeof str}'.`)
    }
    return Buffer.from(str).toString('base64')
  }

  /**
   * Utility function that decodes a base64 string
   * into a normal string using 'utf8' encoding
   * (compatible with the CloudEvent standard).
   *
   * @static
   * @param {?string} obj the string to decode, or '' if not defined or null
   * @return {string} the string decoded from base64
   * @throws {TypeError} if str is not a string instance
   */
  static stringFromBase64 (obj) {
    let str = obj
    if (V.isUndefinedOrNull(obj)) {
      str = ''
    }
    if (!V.isString(str)) {
      throw new TypeError(`Missing or wrong argument: '${str}' must be a string and not a: '${typeof str}'.`)
    }
    return Buffer.from(str, 'base64').toString('utf8')
  }

  /**
   * Utility function that map an Error into an object
   * (compatible with the CloudEvent standard), to fill its 'data' attribute.
   *
   * @static
   * @param {!Error} err the Error to transform
   * @param {object} [options={}] transformation options:
   *        includeStackTrace flag (default false) to add the StackTrace into the object to return,
   *        addStatus flag (default true) to add a 'status' attribute into the object to return,
   *        addTimestamp flag (default false) to add the current 'timestamp' as attribute into the object to return,
   * @return {object} the object representation of the error
   * @throws {TypeError} if err is undefined or null, or is not an Error instance
   */
  static errorToData (err, {
    includeStackTrace = false,
    addStatus = true,
    addTimestamp = false
  } = {}
  ) {
    if (!V.isError(err)) {
      throw new TypeError(`Missing or wrong argument: '${err}' must be an Error and not a: '${typeof err}'.`)
    }
    const data = {
      name: err.name,
      message: err.message,
      stack: (includeStackTrace === true) ? err.stack : null
    }
    if (V.isDefinedAndNotNull(err.code)) {
      data.code = err.code
    }
    if (addStatus === true) {
      data.status = 'error'
    }
    if (addTimestamp === true) {
      data.timestamp = Date.now()
    }
    return data
  }

  /**
   * Utility function that get some process-related info and wrap into an object
   * (compatible with the CloudEvent standard), to fill its 'data' attribute.
   *
   * @static
   * @return {object} the object representation of process-related info data
   */
  static processInfoToData () {
    return {
      hostname: hostname,
      pid: pid
    }
  }

  /**
   * Utility function that strip all arguments (if any) from the given URI/URL string
   * and returns the string without them.
   *
   * @static
   * @param {!string} url the URI/URL (as a string)
   * @param {object} [options={}] containing: strict (boolean, default false) to check it in a more strict way
   * @return {string} the parsed version, but without arguments (if any)
   * @throws {TypeError} if url is undefined or null, or is not a string
   */
  static uriStripArguments (url, { strict = false } = {}) {
    if (!V.isString(url)) {
      throw new TypeError(`Missing or wrong URL: '${url}' must be a string and not a: '${typeof url}'.`)
    }
    if (strict === true) {
      if (!V.isURI(url)) {
        throw new TypeError(`Missing or wrong URL: '${url}'`)
      }
    }
    return url.split('?')[0]
  }

  /**
   * Utility function that merge the given objects (at least one base and another)
   * and returns the new one (but with the prototype of the first).
   *
   * @static
   * @param {!object} base the first object to merge
   * @param {object} others all other(s) object to merge (at least one)
   * @return {object} the new object
   * @throws {TypeError} if base is undefined or null, or is not an object
   */
  static mergeObjects (base, ...others) {
    if (!V.isObject(base)) {
      throw new TypeError(`Missing or wrong argument: '${base}' must be an object and not a: '${typeof base}'.`)
    }
    const baseProto = Object.getPrototypeOf(base)
    return Object.assign(Object.create(baseProto), base, ...others) // set the prototype of the first argument in the clone
  }
}

/**
 * Utility variable that returns the timezone offset value, in msec.
 *
 * @static
 */
Transformer.timezoneOffsetMsec = new Date().getTimezoneOffset() * 60 * 1000

module.exports = Transformer

